import React, { Component } from 'react';
import './App.css';
import Toggle from './Toggle';
import hand from './handNoBg.png';

const baseList = [
  {
    name: 'BIAB',
    hex: '#FFA500',
    price: '5',
    objectID: 0,
  },
  {
    name: 'Hard Gel',
    hex: '#b0e0e6',
    price: '20',
    objectID: 1,
  },
  {
    name: 'Acrylic',
    hex: '#f6f6f6',
    price: '25',
    objectID: 2,
  },
];

const colourList = [
  {
    name: 'Orange Princess',
    hex: '#FFA500',
    price: '5',
    objectID: 0,
  },
  {
    name: 'Michelle',
    hex: '#b0e0e6',
    price: '20',
    objectID: 1,
  },
  {
    name: 'Grey',
    hex: '#f6f6f6',
    price: '25',
    objectID: 2,
  },
  {
    name: 'Red',
    hex: '#FFA500',
    price: '5',
    objectID: 3,
  },
  {
    name: 'Green',
    hex: '#b0e0e6',
    price: '20',
    objectID: 4,
  },
  {
    name: 'Pink',
    hex: '#f6f6f6',
    price: '25',
    objectID: 5,
  },
  {
    name: 'Purple',
    hex: '#f6f6f6',
    price: '25',
    objectID: 6,
  },
  {
    name: 'Yellow',
    hex: '#FFA500',
    price: '5',
    objectID: 7,
  },
  {
    name: 'Brown',
    hex: '#b0e0e6',
    price: '20',
    objectID: 8,
  },
  {
    name: 'Black',
    hex: '#f6f6f6',
    price: '25',
    objectID: 9,
  },
];


class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      baseList: baseList,
      chosenBase: "",
      colourList: colourList,
      chosenColour: "",
    }

    this.onColourPick = this.onColourPick.bind(this);
    this.onBasePick = this.onBasePick.bind(this);
  }

  onColourPick(colour) {
    this.setState({ chosenColour: colour }, () => console.log(this.state.chosenColour));
  }

  onBasePick(base) {
    this.setState({ chosenBase: base }, () => console.log(this.state.chosenBase));
  }

  render() {
    const { colourList, chosenColour } = this.state;

    return (
      <div className="App">
        <div className="interactions">
          <div className="sideBarContainer">
            <Toggle>
              {({ on, toggle }) => (
                <div className="sideBar">
                  {on && <Table
                    list={baseList}
                    onBasePick={this.onBasePick}
                         />}
                  <button onClick={toggle}>Base</button>
                </div>
              )}
            </Toggle>
            <Toggle>
              {({ on, toggle }) => (
                <div className="sideBar">
                  {on && <Table
                    list={colourList}
                         />}
                  <button onClick={toggle}>Colours</button>
                </div>
              )}
            </Toggle>
            <Toggle>
              {({ on, toggle }) => (
                <div className="sideBar">
                  {on && <h2>hi</h2>}
                  <button onClick={toggle}>Shape</button>
                </div>
              )}
            </Toggle>
            <Toggle>
              {({ on, toggle }) => (
                <div className="sideBar">
                  {on && <h2>hi</h2>}
                  <button onClick={toggle}>Length</button>
                </div>
              )}
            </Toggle>
          </div>

          <Visualise
            // base={chosenBase}
            colour={chosenColour}
          ></Visualise>


          <Cost price={chosenColour.price}>
            { chosenColour
              ? <h3>Total</h3>
              : null
            }
          </Cost>

        </div>
      </div>
    );
  }
}

class Table extends Component {
  render() {
    const { onColourPick, onBasePick, list } = this.props;
    return (
      <div>
        {list.map(pick =>
          <div key={pick.objectID} className="colBox">
            <span>
              <div className="colourPeak" style={{ backgroundColor: pick.hex }}></div>
            </span>
            <span>
              <br/>{pick.name}
            </span>
            <Button onClick={() => onColourPick(pick)} type="button">
              Choose
            </Button>
          </div>
        )}
      </div>
    );
  }
}

class Visualise extends Component {
  render() {
    const { chosenColour } = this.props;
    return (
      <div className="visualiser">
        <img src={hand} height="750px" alt="hand-plain"/>

      </div>
    );
  }
}

class Cost extends Component {
  render() {
    const { price, children } = this.props;
    return (
      <div className="bottomBar">
        {children}£{price}
      </div>
    );
  }
}

const Button = ({ onClick, className = '', children }) =>
	  <button
	    onClick={onClick}
	    className={className}
	    type="button"
   >

	    {children}
	  </button>



export default App;
