import React, { Component } from 'react';
import './App.css';

const colourList = [
  {
    name: 'Orange Princess',
    hex: '#FFA500',
    price: '25',
    objectID: 0,
  },
  {
    name: 'Michelle',
    hex: '#b0e0e6',
    price: '25',
    objectID: 1,
  },
  {
    name: 'grey',
    hex: '#f6f6f6',
    price: '25',
    objectID: 2,
  },
];

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      gel: null,
    }

    this.onColourPick = this.onColourPick.bind(this);
  }

  onColourPick(colour) {
    this.setState({ gel: colour.name })
  }

  render() {
    return (
      <div className="App">
        {colourList.map(colour =>
          <form onSubmit={this.onColourPick}>
            <button key={colour.objectID} className="colBox">
              <span>
                <div className="colourPeak" style={{ backgroundColor: colour.hex }}></div>
