import React, { Component } from 'react';

class App extends Component {



  render() {
    // eslint-disable-next-line
    const { chosenColour, chosenBase, chosenShape, handChoice } = this.state;

    return (
        <div className="App">
          <Main />
          <div className="interactions">
            <div className="sideBarContainer">
              <Toggle>
                {({ on, toggle }) => (
                  <div className="sideBar">
                    {on && <Table
                      list={baseList}
                      onPick={this.onPick}
                           />}
                    <button onClick={toggle}>Base</button>
                    {!on && <h1 className="label">1</h1>}
                  </div>
                )}
              </Toggle>
              { this.state.chosenBase.style === 'natural'
                ? null
                : <Toggle>
                  {({ on, toggle }) => (
                    <div className="sideBar">
                      {on && <Table
                        list={shapeList}
                        onPick={this.onPick}
                             />}
                      <button onClick={toggle}>Shape</button>
                      {!on && <h1 className="label">2</h1>}
                    </div>
                  )}
                </Toggle>
              }
              <Toggle>
                {({ on, toggle }) => (
                  <div className="sideBar">
                    {on && <Table
                      list={colourList}
                      onPick={this.onPick}
                           />}
                    <button onClick={toggle}>Colours</button>
                    {!on && <h1 className="label">3</h1>}
                  </div>
                )}
              </Toggle>
              {/* { this.state.chosenBase.style === 'natural'
                ? null
                :  <Toggle>
                {({ on, toggle }) => (
                <div className="sideBar">
                {on &&
                <div className="slidecontainer">
                <input type="range" onChange={this.onLengthChange} step="1" min="1" max="5" value={this.state.value}></input>
                <p>Value: <span id="demo">{this.state.length}</span></p>
                </div>
                }
                <button onClick={toggle}>Length</button>
                {!on && <h1 className="label">4</h1>}
                </div>
                )}
                </Toggle>
              } */}
            </div>


            <Visualise
              // base={chosenBase}
              display={handChoice}>

            </Visualise>




            <Cost price={this.state.basePrice + this.state.shapePrice + this.state.colourPrice}>
              { chosenBase
                ? <h3 id="total">Total</h3>
                : null
              }
            </Cost>

          </div>
        </div>
        );
  }
}

class Table extends Component {
  render() {
    const { onPick, list } = this.props;
    return (
      <div>
        {list.map(pick =>
          <Button onClick={() => onPick(pick)} type="radio" key={pick.objectID} className="colBox">
            <span>
              <div className="colourPeak" style={{ backgroundColor: pick.hex }}></div>
            </span>
            <span>
              <br/>{pick.name}
              <br/>
              <br/>£{pick.price}
            </span>
          </Button>
        )}
      </div>
    );
  }
}

class Visualise extends Component {
  render() {
    const { display } = this.props;
    return (
      <div className="visualiser">
        <img src={ display } id="handPic" height="900px" alt="hand-plain"/>
      </div>
    );
  }
}

class Cost extends Component {
  render() {
    const { price, children } = this.props;
    return (
      <div className="bottomBar">
        <div id="price">
          {children}£{price}
        </div>
        <a href="https://www.instagram.com/ejvnails/">
          <img src={logo} id="logo" width="75em" alt="logo"/>
        </a>
        {/* <p id="promo">Like what you've created? See more of my work over on my <a href="https://www.instagram.com/ejvnails/">Instagram!</a> Or contact me on 07951476008 for more details and to book an appointment!</p> */}
      </div>
    );
  }
}

const Button = ({ onClick, className = '', children }) =>
	  <button
	    onClick={onClick}
	    className={className}
	    type="radio"
   >

	    {children}
	  </button>


export default App;
